import { Card, Container } from 'react-bootstrap';
import { FaStar } from 'react-icons/fa'
import Fetch from './Fetch';
import CardCarousel from './CardCarousel';
import { Link } from 'react-router-dom';

export default function FanFavorite () {

    const uri = 'https://api.themoviedb.org/3/movie/top_rated?language=en-US&page=1';

    return (
        <div>
            <h1>Fan favorites</h1>
            <Container>
                <Fetch 
                    renderSuccess={renderCards}
                    uri={uri}
                />
            </Container>
        </div>
    )

    function renderCards (data) {

        return (
            <CardCarousel 
                renderCard={renderCard}
                data={data.results}
            />
        );
    }

    function renderCard(data) {
        return (
            <Card style={{minWidth: '200px', margin: '5px'}}>
                <Card.Img 
                    src={`https://image.tmdb.org/t/p/w185${data.poster_path}`} 
                />
                <Card.Body>
                <Card.Subtitle>
                    <FaStar style={{color: 'yellow'}}/>
                        {data.vote_average}
                    </Card.Subtitle>
                    <Card.Text>{data.title}</Card.Text>
                </Card.Body>
                <Card.Link href="#">Trailer</Card.Link>
                <Card.Link href="#">Watchlist</Card.Link>
            </Card> 
        )
    }
}
