import Fetch from './Fetch';
import { Carousel } from 'react-bootstrap';
import { Container, Row }  from 'react-bootstrap';

export default function Discover () {

    const uri = 'https://api.themoviedb.org/3/discover/movie?language=en-US&region=US&sort_by=popularity.desc&include_adult=false&include_video=false&with_watch_monetization_types=flatrate';

    return (
        <div className="row discover">
            <h1>Discover</h1>
            <Fetch 
                renderSuccess={movies}
                uri={uri}
            />
        </div>
    );

    function movies(data) {
        return (
            <Container>
                <Row>
                <Carousel className='hero-carousel' fade nextLabel={null} prevLabel={null}>
                    {data.results.map((movie, i) => {
                        return (
                            <Carousel.Item key={i}>
                                <img className="border"
                                    style={{width: '100%'}}
                                    src={`https://image.tmdb.org/t/p/w780${movie.backdrop_path}`}
                                    alt="backdrop"
                                />
                                <Carousel.Caption>
                                    <h2>{movie.title}</h2>
                                    <p>{movie.release_date}</p>
                                </Carousel.Caption>
                            </Carousel.Item>
                        );
                    })}
                </Carousel>
                </Row>
            </Container> 
        );
    }

}
