import { createRef } from 'react';
import { FaRProject } from 'react-icons/fa';
import { ScrollTo, ScrollArea } from 'react-scroll-to';

export default function CardCarousel ({ renderCard, data }) {
    const scrolledDiv = createRef();
    return (
        <div className="card-carousel-container">
            console.log(scroll);
            <ScrollTo>
                {
                    ({scroll}) => (

                        <ScrollArea
                            style={{minWidth: 'fit-content', position: 'absolute'}}
                        >
                            <button
                                style={{zIndex: '1', position: 'sticky', top: '50%', left: '0'}}
                                onClick={() => scroll({ ref: scrolledDiv, x: 500, y: 0, smooth: true })}>
                                    Scroll
                            </button>                            
                        </ScrollArea>
                    )
                }
            </ScrollTo>
            <div 
            ref={scrolledDiv} className="carousel">
                {data.map((card , i) => {
                                return renderCard(card, i);           
                })}
            </div>
            <div
                className="carousel" 
                style={{minWidth: 'fit-content', display: 'flex', overflow: 'scroll'}}
            >
                {data.map((card , i) => {
                    return renderCard(card, i);           
                })}
            </div>
            <button>Prev</button>
            <button>Next</button>
        </div>
    );
}
