import { createRef } from 'react';

export default function CardCarousel ({ renderCard, data }) {
    const scrollDiv = createRef();

    return (
        <div className="card-carousel-container">
            <div
                ref={scrollDiv} 
                className="carousel" 
                style={{minWidth: 'fit-content', display: 'flex', overflow: 'scroll'}}
            >
                {data.map((card , i) => {
                    return renderCard(card, i);           
                })}
            </div>
            <button>Prev</button>
            <button>Next</button>
        </div>
    );
}
