import { Card, Button, Container, Row } from 'react-bootstrap';
import { FaStar } from 'react-icons/fa'
import Fetch from './Fetch';

export default function FanFavorite () {

    const uri = 'https://api.themoviedb.org/3/movie/top_rated?language=en-US&page=1';

    return (
        <div>
            <h1>Fan favorites</h1>
            <Container>
                <Fetch 
                    renderSuccess={renderCards}
                    uri={uri}
                />
            </Container>
        </div>
    )

    function renderCards (data) {
        return (
            <Container>
                <Row>
                {data.results.map((movie , i) => {
                    return (
                        <Card key={i} style={{width: '200px'}}>
                            <Card.Img 
                                src={`https://image.tmdb.org/t/p/w185${movie.poster_path}`} 
                            />
                            <Card.Body>
                                <Card.Title>{movie.title}</Card.Title>
                                <Card.Text>
                                    <FaStar style={{color: 'yellow'}}/>
                                    {movie.vote_average}
                                </Card.Text>
                            </Card.Body>
                            <Card.Link href="#">Trailer</Card.Link>
                            <Card.Link href="#">Watchlist</Card.Link>
                        </Card> 
                    );           
                })}
                </Row>
            </Container>
        );
    }

    const card = () => {
        return (
            <Card style={{width: '200px'}}>
                <Card.Img />
                <Card.Body>
                    <Card.Title>Card Title</Card.Title>
                    <Card.Text>
                        Movie description or something
                    </Card.Text>
                </Card.Body>
                <Button>Watchlist</Button>
            </Card>
        )
    }
}
