import { createRef } from 'react';
import { FaRProject } from 'react-icons/fa';
import { ScrollTo, ScrollArea } from 'react-scroll-to';
import { Container, Row } from 'react-bootstrap';

export default function CardCarousel ({ renderCard, data }) {
    const scrolledDiv = createRef();
    return (
        <div className="card-carousel-container border">
            console.log(scroll);
            <ScrollTo>
                {
                    ({scroll}) => (

                        <ScrollArea
                            className='w-100 d-flex justify-content-between border'
                            style={{position: 'absolute', zIndex: '1'}}
                        >
                            <Container fluid>
                                <Row>
                                <button
                                style={{zIndex: '1', position: 'sticky', top: '50%', left: '0'}}
                                onClick={() => scroll({ ref: scrolledDiv, x: 500, y: 0, smooth: true })}>
                                    Scroll
                            </button> 
                            <button
                                style={{zIndex: '1', position: 'sticky', top: '50%', left: '0'}}
                                onClick={() => scroll({ ref: scrolledDiv, x: 500, y: 0, smooth: true })}>
                                    Scroll
                            </button>  
                                </Row>
                            </Container>

                        </ScrollArea>
                    )
                }
            </ScrollTo>
            <div 
                ref={scrolledDiv} 
                className="carousel"
                style={{display: 'flex', minWidth: 'fit-content', overflow: 'scroll'}}>
                    {data.map((card , i) => {
                                    return renderCard(card, i);           
                    })}
            </div>
        </div>
    );
}
