    }

    return (
        <div className="card-carousel-container border">       
            <div
                className='control-buttons d-flex justify-content-between'
                style={{position: 'relative', zIndex: '1', minHeight: '400px', marginBottom: '-400px'}}
            >
                <button
                    style={{zIndex: '1', position: 'absolute', top: '50%', right: '0'}}
                    onClick={() => {
                        makeScroll();                       
                    }}
                >
                    Next
                </button> 
                <button
                    style={{zIndex: '1', position: 'absolute', top: '50%', left: '0'}}
                    onClick={() => {
                        makeScroll(false); 
                    }}
                >
                    Prev
                </button>                             
            </div>

            <ul 
                ref={scrolledDiv} 
                className="carousel"
                style={{display: 'flex', minWidth: 'fit-content', overflow: 'scroll'}}>
                    {data.map((card , i) => {
                        const ref = createRef();
                        cardRefs.push(ref);
                        return (
                        <li key={i} ref={ref}>
                            {renderCard(card)}           
                        </li>);
                    })}
            </ul>
        </div>
    );
}
