import { createRef } from 'react';

export default function CardCarousel ({ renderCard, data }) {
    const scrollDiv = createRef();
    const text = createRef();

    return (
        <div className="card-carousel-container">
            <div
                ref={scrollDiv} 
                className="carousel" 
                style={{minWidth: 'fit-content', display: 'flex', overflow: 'scroll'}}
            >
                {data.map((card , i) => {
                    return renderCard(card, i);           
                })}
                <div ref={text}>
                    <p>Scrlling here</p>
                </div>
            </div>
            <button onClick={scrollUp}>Prev</button>
            <button onClick={scrollDown}>Next</button>
        </div>
    );

    function scrollUp() {
        text.scrollIntoView();
    }

    function scrollDown() {
        console.log(scrollDiv.scrollTop);
    }
}
