import { createRef } from 'react';
import { GrNext, GrPrevious } from 'react-icons/gr';
import { CardGroup } from 'react-bootstrap';

export default function CardCarousel ({ renderCard, data }) {

    const scrolledDiv = createRef();
    const cardRefs = [];
    var scrollPos = 0;

    const makeScroll = (up) => {
        if(up) 
            scrollPos = scrollPos + 4 > cardRefs.length -1 ? cardRefs.length -1 : scrollPos + 4;
        else 
            scrollPos = scrollPos - 4 < 0 ? 0 : scrollPos - 4;

        console.log(scrollPos);
        cardRefs[scrollPos].current.scrollIntoView({behavior: 'smooth', alignTo: false});
    }

    return (
        <div className="card-carousel-container">       
            <div
                className='control-buttons d-flex justify-content-between'
                style={{position: 'relative', zIndex: '1'}}
            >
                <button
                    style={{zIndex: '1', position: 'absolute', top: '200px', right: '0'}}
                    onClick={() => {
                        makeScroll(true);                       
                    }}
                >
                    <GrNext />
                </button> 
                <button
                    style={{zIndex: '1', position: 'absolute', top: '200px', left: '0'}}
                    onClick={() => {
                        makeScroll(false); 
                    }}
                >
                    <GrPrevious />
                </button>                             
            </div>

            <CardGroup>
            <ul 
                ref={scrolledDiv} 
                className="carousel"
                style={{display: 'flex', minWidth: 'fit-content', overflow: 'hidden', listStyle: 'none'}}>
                    {data.map((card , i) => {
                        const ref = createRef();
                        cardRefs.push(ref);
                        return (
                        <li key={i} ref={ref}>
                            {renderCard(card)}           
                        </li>);
                    })}
            </ul>
            </CardGroup>
        </div>
    );
}
