import { Card } from 'react-bootstrap';
import { FaStar } from 'react-icons/fa'
import Fetch from './Fetch';
import CardCarousel from './CardCarousel';
import { Link } from 'react-router-dom';

export default function FanFavorite () {

    const uri = 'https://api.themoviedb.org/3/movie/top_rated?language=en-US&page=1';

    return (
        <section>
            <h3 className='title'>Fan favorites</h3>            
            <Fetch 
                renderSuccess={renderCards}
                uri={uri}
            />          
        </section>
    )

    function renderCards (data) {

        return (
            <CardCarousel 
                renderCard={renderCard}
                data={data.results}
            />
        );
    }

    function renderCard(data) {
        return (            
            <Card className="cards" style={{minWidth: '160px', height: '375px', margin: '5px'}}>
                <Link to={`/movie/${data.id}`}>
                    <Card.Img
                        src={`https://image.tmdb.org/t/p/w154${data.poster_path}`} 
                    />
                </Link>
                <Card.Body>
                    <Card.Subtitle>
                        <FaStar style={{color: 'yellow'}}/>
                        {data.vote_average}
                    </Card.Subtitle>
                    <Link to='/movie'>
                        <Card.Text>
                            {data.title}
                        </Card.Text>
                    </Link>
                </Card.Body>
                <Card.Link href="#">Trailer</Card.Link>
                <Card.Link href="#">Watchlist</Card.Link>
            </Card>             
        );
    }
}
