import { createRef, useState } from 'react';
import { FaRProject } from 'react-icons/fa';
import { ScrollTo, ScrollArea } from 'react-scroll-to';
import { Container, Row } from 'react-bootstrap';

export default function CardCarousel ({ renderCard, data }) {

    const scrolledDiv = createRef();
    var scrollPos = 0;

    const makeScroll = (x, scroll) => {
        console.log(scrolledDiv.style.width);
        scrollPos += x;
        scroll({ ref: scrolledDiv, x: scrollPos, y: 0, smooth: true })
    }

    return (
        <div className="card-carousel-container border">

            <ScrollTo>
                {
                    ({scroll}) => (

                        <ScrollArea
                            className='d-flex justify-content-between'
                            style={{position: 'relative', zIndex: '1', minHeight: '400px', marginBottom: '-400px'}}
                        >
                            <button
                                style={{zIndex: '1', position: 'absolute', top: '50%', right: '0'}}
                                onClick={() => {
                                    makeScroll(500, scroll);                               
                                    // scroll({ ref: scrolledDiv, x: scrollPos, y: 0, smooth: true })
                                }}
                            >
                                Next
                            </button> 
                            <button
                                style={{zIndex: '1', position: 'absolute', top: '50%', left: '0'}}
                                onClick={() => {
                                    makeScroll(-500, scroll); 
                                    // scroll({ ref: scrolledDiv, x: scrollPos, y: 0, smooth: true })
                                }}
                            >
                                Prev
                            </button>                             
                        </ScrollArea>
                    )
                }
            </ScrollTo>
            <div 
                ref={scrolledDiv} 
                className="carousel"
                style={{display: 'flex', minWidth: 'fit-content', overflow: 'scroll'}}>
                    {data.map((card , i) => {
                                    return renderCard(card, i);           
                    })}
            </div>
        </div>
    );
}
