export default function CardCarousel ({ renderCard, data }) {
    return (
        <div className="card-carousel-container">
            <div 
                className="carousel" 
                style={{'min-width': 'fit-content', display: 'flex', overflow: 'scroll'}}
            >
                {data.map((card , i) => {
                    return renderCard(card);           
                })}
            </div>
            <button>Prev</button>
            <button onClick={scroll}>Next</button>
        </div>
    );
}
