import { Form, FormControl, Button } from 'react-bootstrap';
import { FaSearch } from 'react-icons/fa';
import { useState, useEffect, useRef } from 'react';
import Fetch from './Fetch';
import List from './List';

export default function SearchForm() {

    const uri = 'https://api.themoviedb.org/3/search/movie?language=en-US&page=1&include_adult=true';
    const [input, setInput] = useState('');
    const [showSuggestions, setShowSuggestions] = useState(false);
    const firstUpdate = useRef(true);

    useEffect(() => {
        if(firstUpdate.current){
            firstUpdate.current = false;
            return;
        }

        setShowSuggestions(false);
        const delay = setTimeout(() => {
            setShowSuggestions(true)
        }, 1000)

        return () => clearTimeout(delay);
    }, [input]);

    const handleSubmit = (e) => {
        e.preventDefault();
    }

    const renderResults = (data) => {
        if(input === '') return;

        const movies = data.results.splice(0,5);
        console.log(movies);
        return (
            <div style={{position: 'absolute', top: '50px', left: '0', border: 'solid'}}>
                <p>Results here</p>
                <List 
                    items={movies}
                    renderItem={
                        (movie) => {

                        }
                    }
                />
            </div>
        )
    }

    return (
        <Form 
            inline 
            className="search-form d-flex w-100"
            style={{position: 'relative'}}>
            <FormControl
                type="text"
                placeholder="Search"
                className="w-100"
                value={input}
                onChange={e => setInput(e.target.value)}
                required
            />
            <Button type="submit" onClick={handleSubmit}>
                <FaSearch />
            </Button>
            {showSuggestions && (
                <Fetch 
                    uri={uri + `&query=${input}`}
                    renderSuccess={renderResults}
                />
            )}
        </Form>
    );
}
